import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import PortfolioSummary from './PortfolioSummary';
import AIChatInput from './AIChatInput';
import MarketDataCards from './MarketDataCards';
import NewsTopHoldings from './NewsTopHoldings';
import QuickTradeForm from './QuickTradeForm';

const Dashboard = () => {
  const navigate = useNavigate();
  const userName = localStorage.getItem('userName') || 'User';
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('userName');
    navigate('/login');
  };

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  return (
    <div className="dashboard-container">
      <header style={{ display: 'flex', justifyContent: 'flex-end', padding: '1rem', backgroundColor: '#1f2937', color: 'white', position: 'relative' }}>
        <div style={{ cursor: 'pointer', position: 'relative' }} onClick={toggleDropdown} aria-label="User menu" tabIndex={0} onKeyDown={(e) => { if (e.key === 'Enter') toggleDropdown(); }}>
          <span role="img" aria-label="profile icon" style={{ fontSize: '1.5rem' }}>ðŸ‘¤</span>
        </div>
        {dropdownOpen && (
          <div style={{
            position: 'absolute',
            top: '3rem',
            right: '1rem',
            backgroundColor: '#374151',
            borderRadius: '0.375rem',
            boxShadow: '0 4px 6px rgba(0,0,0,0.1)',
            width: '200px',
            zIndex: 1000,
            color: 'white',
            padding: '0.5rem 0'
          }}>
            <div style={{ padding: '0.5rem 1rem', borderBottom: '1px solid #4b5563' }}>
              <strong>{userName}</strong>
            </div>
            <button
              onClick={() => { /* Placeholder for settings action */ }}
              style={{
                width: '100%',
                padding: '0.5rem 1rem',
                background: 'none',
                border: 'none',
                textAlign: 'left',
                cursor: 'pointer',
                color: 'white'
              }}
            >
              Settings
            </button>
            <button
              onClick={handleLogout}
              style={{
                width: '100%',
                padding: '0.5rem 1rem',
                backgroundColor: '#dc2626',
                border: 'none',
                color: 'white',
                fontWeight: 'bold',
                cursor: 'pointer',
                borderRadius: '0 0 0.375rem 0.375rem'
              }}
            >
              Logout
            </button>
          </div>
        )}
      </header>
      <main className="min-h-screen bg-[#0F172A] p-6 font-sans">
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <div className="md:col-span-2 space-y-6">
            <PortfolioSummary />
            <MarketDataCards />
            <NewsTopHoldings />
          </div>
          <div className="space-y-6">
            <AIChatInput />
            <QuickTradeForm />
          </div>
        </div>
      </main>
    </div>
  );
};

export default Dashboard;
