import React, { useState } from 'react';

const Register = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
  });

  const { username, email, password } = formData;

  const onChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('/api/auth/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, email, password }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        alert(`Registration failed: ${errorData.error || 'Unknown error'}`);
        return;
      }

      const data = await response.json();
      alert('Registration successful!');
      // Optionally redirect or clear form
    } catch (error) {
      alert(`Registration failed: ${error.message}`);
    }
  };

  return (
    <form onSubmit={onSubmit} className="max-w-md mx-auto p-6 bg-white rounded shadow-md" style={{ backgroundColor: '#F9FAFB' }}>
      <div className="mb-4">
        <label htmlFor="username" className="block text-[#111827] font-bold mb-2">Username:</label>
        <input
          type="text"
          id="username"
          name="username"
          value={username}
          onChange={onChange}
          required
          className="w-full px-3 py-2 border border-[#D1D5DB] rounded focus:outline-none focus:ring focus:border-[#4F46E5]"
          style={{ backgroundColor: '#FFFFFF', color: '#111827' }}
        />
      </div>
      <div className="mb-4">
        <label htmlFor="email" className="block text-[#111827] font-bold mb-2">Email:</label>
        <input
          type="email"
          id="email"
          name="email"
          value={email}
          onChange={onChange}
          required
          className="w-full px-3 py-2 border border-[#D1D5DB] rounded focus:outline-none focus:ring focus:border-[#4F46E5]"
          style={{ backgroundColor: '#FFFFFF', color: '#111827' }}
        />
      </div>
      <div className="mb-6">
        <label htmlFor="password" className="block text-[#111827] font-bold mb-2">Password:</label>
        <input
          type="password"
          id="password"
          name="password"
          value={password}
          onChange={onChange}
          required
          className="w-full px-3 py-2 border border-[#D1D5DB] rounded focus:outline-none focus:ring focus:border-[#4F46E5]"
          style={{ backgroundColor: '#FFFFFF', color: '#111827' }}
        />
      </div>
      <button
        type="submit"
        className="w-full font-bold py-2 px-4 rounded hover:bg-[#6366F1] transition duration-200"
        style={{ backgroundColor: '#4F46E5', color: '#FFFFFF' }}
      >
        Register
      </button>
    </form>
  );
};

export default Register;
