import React, { useEffect, useState } from 'react';
import axios from 'axios';

function NewsTopHoldings() {
  const [news, setNews] = useState([]);
  const [topHoldings, setTopHoldings] = useState([
    { stockSymbol: 'AAPL', quantity: 50 },
    { stockSymbol: 'GOOGL', quantity: 30 },
    { stockSymbol: 'TSLA', quantity: 20 },
  ]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/news')
      .then(response => {
        setNews(response.data);
      })
      .catch(error => {
        console.error('Error fetching news:', error);
      });
  }, []);

  return (
    <div className="bg-[#1E293B] p-6 rounded-xl text-white shadow-lg space-y-6">
      <div>
        <h2 className="text-xl font-semibold mb-4">Latest News</h2>
        <ul className="list-disc list-inside max-h-48 overflow-y-auto text-gray-300">
          {news.map((item) => (
            <li key={item._id} className="mb-2">
              <a href={item.url} target="_blank" rel="noopener noreferrer" className="text-blue-400 hover:underline">
                {item.title}
              </a>
              <p className="text-sm">{item.source} - {new Date(item.publishedAt).toLocaleDateString()}</p>
            </li>
          ))}
        </ul>
      </div>
      <div>
        <h2 className="text-xl font-semibold mb-4">Top Holdings</h2>
        <ul>
          {topHoldings.map((holding) => (
            <li key={holding.stockSymbol} className="flex justify-between">
              <span>{holding.stockSymbol}</span>
              <span>{holding.quantity} shares</span>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default NewsTopHoldings;
