import React, { useEffect, useState } from 'react';
import axios from 'axios';

function LatestFinancialNews() {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchNews = async () => {
      try {
        setLoading(true);
        setError(null);
        const response = await axios.get('/api/latest');
        setNews(response.data);
      } catch (err) {
        setError('Failed to load news. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchNews();
  }, []);

  const formatTimeAgo = (dateString) => {
    if (!dateString) return '';
    const date = new Date(dateString);
    const now = new Date();
    const diffMs = now - date;
    const diffMinutes = Math.floor(diffMs / (1000 * 60));
    if (diffMinutes < 1) return 'just now';
    if (diffMinutes < 60) return `${diffMinutes} minute${diffMinutes > 1 ? 's' : ''} ago`;
    const diffHours = Math.floor(diffMinutes / 60);
    if (diffHours < 24) return `${diffHours} hour${diffHours > 1 ? 's' : ''} ago`;
    const diffDays = Math.floor(diffHours / 24);
    return `${diffDays} day${diffDays > 1 ? 's' : ''} ago`;
  };

  return (
    <div className="bg-[#1E293B] p-6 rounded-xl text-white shadow-lg max-h-96 overflow-y-auto">
      <h2 className="text-xl font-semibold mb-4">Latest Financial News</h2>
      {loading && <p>Loading news...</p>}
      {error && <p className="text-red-500">{error}</p>}
      {!loading && !error && (
        <ul className="space-y-3">
          {news.length === 0 && <li>No news available.</li>}
          {news.map((item) => (
            <li key={item.id || item._id} className="hover:bg-[#0F172A] p-2 rounded cursor-pointer">
              <a
                href={item.url}
                target="_blank"
                rel="noopener noreferrer"
                className="text-blue-400 font-medium hover:underline"
              >
                {item.title}
              </a>
              <div className="text-gray-400 text-sm">
                {item.source} â€“ {formatTimeAgo(item.published)}
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default LatestFinancialNews;
