import React from 'react';
import PortfolioSummary from './PortfolioSummary';
import MarketDataCards from './MarketDataCards';
import AIChatInput from './AIChatInput';
import LatestFinancialNews from './LatestFinancialNews';
import TopHoldings from './TopHoldings';
import QuickTradeForm from './QuickTradeForm';

const Dashboard = () => {
  const userName = localStorage.getItem('userName') || 'John Doe';
  const virtualBalance = '98,765.43'; // This can be dynamic if available

  return (
    <div className="min-h-screen bg-[#0F172A] text-white font-sans">
      {/* Top Navigation Bar */}
      <nav className="flex items-center justify-between px-8 py-4 bg-[#1E293B] shadow-lg">
        <div className="text-3xl font-extrabold tracking-wide">
          VirtualTrade-<span className="text-[#00BFFF]">X</span>
        </div>
        <div className="flex-1 mx-8 max-w-xl">
          <input
            type="text"
            placeholder="Search for a stock (e.g., AAPL, MSFT)..."
            className="w-full rounded-lg px-5 py-3 text-black placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-[#00BFFF] transition-shadow"
          />
        </div>
        <div className="flex items-center space-x-8">
          <div className="text-right">
            <div className="text-sm uppercase tracking-wider text-gray-400">Virtual USD Balance</div>
            <div className="text-xl font-semibold">{virtualBalance} Virtual USD</div>
          </div>
          <div className="flex items-center space-x-3 cursor-pointer hover:bg-[#334155] rounded-full px-3 py-1 transition-colors">
            <div className="w-10 h-10 rounded-full bg-gray-600 flex items-center justify-center text-white font-bold text-lg select-none">
              {userName.charAt(0).toUpperCase()}
            </div>
            <div className="text-lg font-medium">{userName}</div>
          </div>
        </div>
      </nav>

      {/* Dashboard Sections */}
      <main className="p-8 grid grid-cols-1 md:grid-cols-3 gap-8 max-w-7xl mx-auto">
        {/* Left Column */}
        <div className="space-y-8">
          {/* My Portfolio Card */}
          <div className="bg-[#1E293B] rounded-xl shadow-xl p-6">
            <PortfolioSummary />
          </div>

          {/* Top Holdings Card */}
          <div className="bg-[#1E293B] rounded-xl shadow-xl p-6">
            <TopHoldings />
          </div>
        </div>

        {/* Center Column */}
        <div className="space-y-8">
          {/* Market Highlights Card */}
          <div className="bg-[#1E293B] rounded-xl shadow-xl p-6">
            <MarketDataCards />
          </div>

          {/* Latest Financial News Card */}
          <div className="bg-[#1E293B] rounded-xl shadow-xl p-6">
            <LatestFinancialNews />
          </div>
        </div>

        {/* Right Column */}
        <div className="space-y-8">
          {/* VirtualTradeX AI Card */}
          <div className="bg-[#1E293B] rounded-xl shadow-xl p-6 flex flex-col h-full">
            <h2 className="text-2xl font-semibold mb-6">VTX AI</h2>
            <AIChatInput />
          </div>

          {/* Quick Trade Card */}
          <div className="bg-[#1E293B] rounded-xl shadow-xl p-6">
            <QuickTradeForm />
          </div>
        </div>
      </main>
    </div>
  );
};

export default Dashboard;
