import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import PortfolioSummary from './PortfolioSummary';
import AIChatInput from './AIChatInput';
import MarketDataCards from './MarketDataCards';
import NewsTopHoldings from './NewsTopHoldings';
import QuickTradeForm from './QuickTradeForm';

const Dashboard = () => {
  const navigate = useNavigate();
  const userName = localStorage.getItem('userName') || 'User';
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('userName');
    navigate('/login');
  };

  return (
    <div className="dashboard-container">
      <header style={{ display: 'flex', justifyContent: 'flex-end', padding: '1rem', backgroundColor: '#1f2937', color: 'white' }}>
        <div style={{ marginRight: '1rem' }}>Welcome, {userName}</div>
        <button
          onClick={handleLogout}
          style={{
            backgroundColor: '#dc2626',
            color: 'white',
            fontWeight: 'bold',
            padding: '0.5rem 1rem',
            borderRadius: '0.375rem',
            border: 'none',
            cursor: 'pointer'
          }}
        >
          Logout
        </button>
      </header>
      <main className="min-h-screen bg-[#0F172A] p-6 font-sans">
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <div className="md:col-span-2 space-y-6">
            <PortfolioSummary />
            <MarketDataCards />
            <NewsTopHoldings />
          </div>
          <div className="space-y-6">
            <AIChatInput />
            <QuickTradeForm />
          </div>
        </div>
      </main>
    </div>
  );
};

export default Dashboard;
