import React, { useEffect, useState } from 'react';
import axios from 'axios';

function PortfolioSummary() {
  const [portfolio, setPortfolio] = useState(null);

  useEffect(() => {
    // Fetch mock portfolio data from backend
    axios.get('http://localhost:5000/api/portfolio')
      .then(response => {
        setPortfolio(response.data);
      })
      .catch(error => {
        console.error('Error fetching portfolio data:', error);
      });
  }, []);

  if (!portfolio) {
    return <div>Loading portfolio...</div>;
  }

  return (
    <div className="bg-white p-4 rounded shadow">
      <h2 className="text-xl font-semibold mb-4">Portfolio Summary</h2>
      <ul>
        {portfolio.stocks.map((stock) => (
          <li key={stock.stockSymbol} className="mb-2">
            <span className="font-bold">{stock.stockSymbol}</span>: {stock.quantity} shares @ ${stock.averagePrice.toFixed(2)}
          </li>
        ))}
      </ul>
      <p className="text-sm text-gray-500 mt-2">Last updated: {new Date(portfolio.lastUpdated).toLocaleString()}</p>
    </div>
  );
}

export default PortfolioSummary;
