import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';

const Login = () => {
  const [formData, setFormData] = useState({ email: '', password: '' });
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');
    setError('');
    try {
      const response = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });
      if (response.ok) {
        const data = await response.json();
        localStorage.setItem('token', data.token);
        setMessage('Login successful.');
        setFormData({ email: '', password: '' });
        navigate('/dashboard');
      } else {
        const data = await response.json();
        setError(data.error || 'Login failed');
      }
    } catch (err) {
      setError('Login failed: ' + err.message);
    }
  };

  return (
    <>
      <style>{`
        .trading-bg {
          background: radial-gradient(circle at center, #0F172A, #000);
          overflow: hidden;
          position: relative;
          animation: pulse 10s ease-in-out infinite;
        }

        @keyframes pulse {
          0%, 100% { background-color: #0F172A; }
          50% { background-color: #1E293B; }
        }

        .candles {
          position: absolute;
          top: 0;
          left: 0;
          width: 200%;
          height: 100%;
          background: repeating-linear-gradient(
            to right,
            rgba(59, 130, 246, 0.2) 0,
            rgba(59, 130, 246, 0.2) 2px,
            transparent 2px,
            transparent 6px
          );
          animation: scroll 20s linear infinite;
          opacity: 0.2;
        }

        @keyframes scroll {
          0% { transform: translateX(0); }
          100% { transform: translateX(-50%); }
        }

        .tagline {
          font-size: 1rem;
          color: #38BDF8;
          margin-bottom: 1rem;
          text-align: center;
        }
      `}</style>

      <div className="trading-bg min-h-screen flex items-center justify-center">
        <div className="candles"></div>

        <div className="max-w-md w-full p-6 bg-[#1E293B] rounded shadow-lg border border-[#334155] z-10">
          <h2 className="text-2xl font-bold mb-2 text-center text-[#F1F5F9]">Login</h2>
          <p className="tagline">Access the Markets. Trade Smarter.</p>

          {message && <p className="text-[#22C55E] mb-4">{message}</p>}
          {error && <p className="text-[#EF4444] mb-4">{error}</p>}

          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label className="block mb-1 font-semibold text-[#94A3B8]">Email:</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                required
                className="w-full px-3 py-2 border rounded border-[#334155] bg-[#0F172A] text-[#F1F5F9] focus:outline-none focus:ring-2 focus:ring-[#3B82F6]"
              />
            </div>
            <div>
              <label className="block mb-1 font-semibold text-[#94A3B8]">Password:</label>
              <input
                type="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                required
                className="w-full px-3 py-2 border rounded border-[#334155] bg-[#0F172A] text-[#F1F5F9] focus:outline-none focus:ring-2 focus:ring-[#3B82F6]"
              />
            </div>
            <button
              type="submit"
              className="w-full bg-[#3B82F6] text-[#F1F5F9] py-2 rounded hover:bg-blue-700 transition"
            >
              Login
            </button>
          </form>

          <p className="mt-4 text-center text-[#94A3B8]">
            Don't have an account?{' '}
            <Link to="/register" className="text-[#3B82F6] hover:underline">
              Register
            </Link>
          </p>
        </div>
      </div>
    </>
  );
};

export default Login;
