import React, { useEffect, useState } from 'react';
import axios from 'axios';

function PortfolioSummary() {
  const [portfolio, setPortfolio] = useState(null);

  useEffect(() => {
    // Fetch portfolio data from backend
    axios.get('http://localhost:5000/api/portfolio')
      .then(response => {
        // Assuming response.data is an array of portfolios, take the first or aggregate as needed
        if (Array.isArray(response.data) && response.data.length > 0) {
          setPortfolio(response.data[0]);
        } else {
          setPortfolio(null);
        }
      })
      .catch(error => {
        console.error('Error fetching portfolio data:', error);
        setPortfolio(null);
      });
  }, []);

  if (!portfolio) {
    return <div>No portfolio data available.</div>;
  }

  return (
    <div className="bg-[#1E293B] p-6 rounded-xl text-white shadow-lg">
      <h2 className="text-xl font-semibold mb-4">Portfolio Summary</h2>
      <ul>
        {portfolio.stocks && portfolio.stocks.length > 0 ? (
          portfolio.stocks.map((stock) => (
            <li key={stock.stockSymbol} className="flex justify-between mb-2">
              <span>{stock.stockSymbol}</span>
              <span>{stock.quantity} shares @ <span className="font-bold">${stock.averagePrice.toFixed(2)}</span></span>
            </li>
          ))
        ) : (
          <li>No stocks available in portfolio.</li>
        )}
      </ul>
      <p className="text-gray-300 text-sm mt-2">Last updated: {portfolio.lastUpdated ? new Date(portfolio.lastUpdated).toLocaleString() : 'N/A'}</p>
    </div>
  );
}

export default PortfolioSummary;
