import React, { useEffect, useState } from 'react';
import axios from 'axios';

function MarketDataCards() {
  const [marketData, setMarketData] = useState([]);
  const [activeTab, setActiveTab] = useState('Top Gainers');
  const tabs = ['Top Gainers', 'Top Losers', 'Most Active'];

  useEffect(() => {
    const fetchMarketData = async () => {
      try {
        const requests = symbols.map(symbol =>
          axios.get(`https://finnhub.io/api/v1/quote`, {
            params: { symbol, token: apiKey }
          })
          .then(response => ({
            stockSymbol: symbol,
            price: response.data.c,
            change: response.data.d,
            percentChange: response.data.dp
          }))
        );
        const results = await Promise.all(requests);
        setMarketData(results);
      } catch (error) {
        console.error('Error fetching market data from Finnhub:', error);
      }
    };

    fetchMarketData();
  }, []);

  return (
    <div className="bg-[#1E293B] p-6 rounded-xl text-white shadow-lg">
      <h2 className="text-xl font-semibold mb-4">Market Highlights</h2>
      <div className="grid grid-cols-5 gap-4">
        {marketData.slice(0, 5).map((item) => (
          <div key={item.stockSymbol} className="border border-gray-700 p-3 rounded-md">
            <h3 className="font-bold">{item.stockSymbol}</h3>
            <p>Price: ${item.price !== null ? item.price.toFixed(2) : 'N/A'}</p>
            <p>Change: {item.change !== null ? item.change.toFixed(2) : 'N/A'}</p>
            <p>Chg %: {item.percentChange !== null ? item.percentChange.toFixed(2) : 'N/A'}%</p>
          </div>
        ))}
      </div>
      <div className="grid grid-cols-5 gap-4 mt-4">
        {marketData.slice(5, 10).map((item) => (
          <div key={item.stockSymbol} className="border border-gray-700 p-3 rounded-md">
            <h3 className="font-bold">{item.stockSymbol}</h3>
            <p>Price: ${item.price !== null ? item.price.toFixed(2) : 'N/A'}</p>
            <p>Change: {item.change !== null ? item.change.toFixed(2) : 'N/A'}</p>
            <p>Chg %: {item.percentChange !== null ? item.percentChange.toFixed(2) : 'N/A'}%</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default MarketDataCards;
