import React, { useEffect, useState } from 'react';
import axios from 'axios';

function PortfolioSummary() {
  const [topHoldings, setTopHoldings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchTopHoldings = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/portfolio/top-holdings');
        setTopHoldings(response.data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching top holdings:', err);
        setError('Failed to load top holdings');
        setLoading(false);
      }
    };

    fetchTopHoldings();
  }, []);

  if (loading) {
    return <div>Loading top holdings...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  if (!topHoldings || topHoldings.length === 0) {
    return <div>No top holdings data available.</div>;
  }

  return (
    <div className="bg-[#1E293B] p-6 rounded-xl text-white shadow-lg">
      <h2 className="text-xl font-semibold mb-4">Top Holdings</h2>
      <ul>
        {topHoldings.map((holding) => (
          <li key={holding.stockSymbol} className="flex justify-between mb-2">
            <span>{holding.stockSymbol}</span>
            <span>{holding.quantity} shares</span>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default PortfolioSummary;
