import React from 'react';
import PortfolioSummary from './PortfolioSummary';
import MarketDataCards from './MarketDataCards';
import AIChatInput from './AIChatInput';
import LatestFinancialNews from './LatestFinancialNews';
import TopHoldings from './TopHoldings';
import QuickTradeForm from './QuickTradeForm';
import Watchlist from './Watchlist';
import ChartCard from './ChartCard';

const Dashboard = () => {
  const userName = localStorage.getItem('userName') || 'John Doe';
  const virtualBalance = '98,765.43'; // This can be dynamic if available

  return (
    <div className="min-h-screen bg-[#0F172A] text-white font-sans">
      {/* Top Navigation Bar */}
      <nav className="h-16 w-full flex items-center justify-between px-6 bg-[#1E293B] shadow-md">
        <div className="text-2xl font-bold">
          VirtualTrade-<span className="text-[#00BFFF]">X</span>
        </div>
        <div className="flex-1 mx-6">
          <input
            type="text"
            placeholder="Search for a stock (e.g., AAPL, MSFT)..."
            className="w-full rounded-md px-4 py-2 text-black focus:outline-none"
          />
        </div>
        <div className="flex items-center space-x-6">
          <div className="text-right">
            <div className="text-sm">Virtual USD Balance</div>
            <div className="text-lg font-semibold">{virtualBalance} Virtual USD</div>
          </div>
          <div className="flex items-center space-x-2 cursor-pointer">
            <div className="w-8 h-8 rounded-full bg-gray-600 flex items-center justify-center text-white font-semibold">
              {userName.charAt(0).toUpperCase()}
            </div>
            <div>{userName}</div>
          </div>
        </div>
      </nav>

      {/* Dashboard Sections */}
      <main className="p-6 grid grid-cols-12 gap-6">
        {/* Row 2: Portfolio Summary, News, Top Holdings */}
        <div className="col-span-4 h-[300px]">
          <PortfolioSummary />
        </div>
        <div className="col-span-4 h-[300px]">
          <LatestFinancialNews />
        </div>
        <div className="col-span-4 h-[300px]">
          <TopHoldings />
        </div>

        {/* Row 3: AI Assistant, Watchlist */}
        <div className="col-span-6 h-[250px] bg-[#1E293B] rounded-lg shadow-lg p-6 flex flex-col">
          <h2 className="text-xl font-semibold mb-4">VirtualTradeX AI</h2>
          <AIChatInput />
        </div>
        <div className="col-span-6 h-[250px]">
          <Watchlist />
        </div>

        {/* Row 4: Chart Card */}
        <div className="col-span-12 h-[400px]">
          <ChartCard />
        </div>
      </main>
    </div>
  );
};

export default Dashboard;
