import React, { useState } from 'react';
import axios from 'axios';

function AIChatInput() {
  const [input, setInput] = useState('');
  const [response, setResponse] = useState('');
  const [isOpen, setIsOpen] = useState(false);

  const toggleChat = () => {
    setIsOpen(!isOpen);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const apiKey = 'AIzaSyBbzgqq7-fnK8H9DaNnGeUXmdqH8913ldQ';
      const baseUrl = 'https://generativelanguage.googleapis.com/v1beta/models/chat-bison:generateMessage';

      const res = await axios.post(
        baseUrl,
        {
          prompt: {
            messages: [{ content: input, role: 'user' }]
          }
        },
        {
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${apiKey}`
          }
        }
      );
      setResponse(res.data.candidates[0].content);
    } catch (error) {
      console.error('Error communicating with Gemini AI service:', error);
      setResponse('Error communicating with AI service.');
    }
  };

  return (
    <div className="fixed bottom-4 right-4 flex flex-col items-center">
      <button
        onClick={toggleChat}
        className="bg-blue-600 text-white p-3 rounded-full shadow-lg hover:bg-blue-700 focus:outline-none"
        aria-label="Toggle AI Chat"
      >
        <svg
          xmlns="http://www.w3.org/2000/svg"
          className="h-6 w-6"
          fill="none"
          viewBox="0 0 24 24"
          stroke="currentColor"
          strokeWidth={2}
        >
          <path strokeLinecap="round" strokeLinejoin="round" d="M12 4v16m8-8H4" />
        </svg>
      </button>
      {isOpen && (
        <div className="mt-2 w-80 bg-[#1E293B] p-4 rounded-xl text-white shadow-lg">
          <h2 className="text-xl font-semibold mb-4 text-center">AI Chat</h2>
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              className="w-full bg-gray-700 text-white p-2 rounded-md mb-2"
              placeholder="Ask AI..."
              value={input}
              onChange={(e) => setInput(e.target.value)}
            />
            <button
              type="submit"
              className="bg-blue-600 hover:opacity-80 transition-all duration-300 text-white px-4 py-2 rounded-md w-full"
            >
              Send
            </button>
          </form>
          {response && <p className="mt-4 text-gray-300">{response}</p>}
        </div>
      )}
    </div>
  );
}

export default AIChatInput;
