import React, { useState } from 'react';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  LineElement,
  CategoryScale,
  LinearScale,
  PointElement,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(LineElement, CategoryScale, LinearScale, PointElement, Tooltip, Legend);

const TIME_RANGES = [
  { label: '1D', key: '1D' },
  { label: '1W', key: '1W' },
  { label: '1M', key: '1M' },
  { label: '3M', key: '3M' },
  { label: 'YTD', key: 'YTD' },
  { label: 'ALL', key: 'ALL' },
];

// Dummy data for each time range
const chartDataByRange = {
  '1D': [0, 0, 0, 0, 0, 0, 0],
  '1W': [0, 0, 0, 0, 0, 0, 0],
  '1M': [0, 0, 0, 0, 0, 0, 0],
  '3M': [0, 0, 0, 0, 0, 0, 0],
  'YTD': [0, 0, 0, 0, 0, 0, 0],
  'ALL': [0, 0, 0, 0, 0, 0, 0],
};

const PortfolioSummary = () => {
  const [activeRange, setActiveRange] = useState('1D');

  const data = {
    labels: Array(chartDataByRange[activeRange].length).fill(''),
    datasets: [
      {
        label: 'Portfolio Value',
        data: chartDataByRange[activeRange],
        fill: false,
        borderColor: '#38bdf8',
        backgroundColor: '#38bdf8',
        tension: 0.3,
        pointRadius: 0,
      },
    ],
  };

  const options = {
    plugins: {
      legend: { display: false },
      tooltip: { enabled: true },
    },
    scales: {
      x: { display: false },
      y: { display: false },
    },
    elements: {
      line: { borderWidth: 2 },
    },
    responsive: true,
    maintainAspectRatio: false,
  };

  return (
    <div className="bg-[#1E293B] rounded-2xl shadow-lg p-6">
      <div className="flex items-center justify-between mb-2">
        <div>
          <div className="text-lg font-semibold">My Portfolio</div>
          <div className="text-2xl font-bold mt-1">$0</div>
          <div className="text-green-400 text-sm">+$0 (0.0%)</div>
        </div>
        <div className="flex space-x-2">
          {TIME_RANGES.map((range) => (
            <button
              key={range.key}
              className={`px-2 py-1 rounded text-xs font-medium transition ${
                activeRange === range.key
                  ? 'bg-[#38bdf8] text-[#0F172A]'
                  : 'bg-[#334155] text-gray-300 hover:bg-[#475569]'
              }`}
              onClick={() => setActiveRange(range.key)}
            >
              {range.label}
            </button>
          ))}
        </div>
      </div>
      <div className="h-32 mt-2">
        <Line data={data} options={options} />
      </div>
    </div>
  );
};

export default PortfolioSummary;
