import React, { useState } from 'react';
import axios from 'axios';

function AIChatInput() {
  const [input, setInput] = useState('');
  const [response, setResponse] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const apiKey = 'AIzaSyBbzgqq7-fnK8H9DaNnGeUXmdqH8913ldQ';
      const baseUrl = 'https://generativelanguage.googleapis.com/v1beta/models/chat-bison:generateMessage';

      const res = await axios.post(
        baseUrl,
        {
          prompt: {
            messages: [{ content: input, role: 'user' }]
          }
        },
        {
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${apiKey}`
          }
        }
      );
      setResponse(res.data.candidates[0].content);
    } catch (error) {
      console.error('Error communicating with Gemini AI service:', error);
      setResponse('Error communicating with AI service.');
    }
  };

  return (
    <div className="bg-[#1E293B] p-6 rounded-xl text-white shadow-lg">
      <h2 className="text-xl font-semibold mb-4">AI Chat</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          className="w-full bg-gray-700 text-white p-2 rounded-md mt-2"
          placeholder="Ask AI..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
        />
        <button
          type="submit"
          className="bg-blue-600 hover:opacity-80 transition-all duration-300 text-white px-4 py-2 rounded-md mt-2"
        >
          Send
        </button>
      </form>
      {response && <p className="mt-4 text-gray-300">{response}</p>}
    </div>
  );
}

export default AIChatInput;
