import React, { useState } from 'react';
import axios from 'axios';

function AIChatInput() {
  const [input, setInput] = useState('');
  const [response, setResponse] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/ai/chat', { message: input });
      setResponse(res.data.reply);
    } catch (error) {
      console.error('Error sending AI chat message:', error);
      setResponse('Error communicating with AI service.');
    }
  };

  return (
    <div className="bg-white p-4 rounded shadow">
      <h2 className="text-xl font-semibold mb-4">AI Chat</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          className="w-full border border-gray-300 rounded px-3 py-2 mb-2"
          placeholder="Ask AI..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
        />
        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded">
          Send
        </button>
      </form>
      {response && <p className="mt-4 text-gray-700">{response}</p>}
    </div>
  );
}

export default AIChatInput;
