import React, { useEffect, useState } from 'react';
import axios from 'axios';

function NewsTopHoldings() {
  const [news, setNews] = useState([]);
  const [topHoldings, setTopHoldings] = useState([]);

  useEffect(() => {
    const fetchNews = async () => {
      try {
        const response = await axios.get('https://api.currentsapi.services/v1/latest-news', {
          params: {
            apiKey: 'y2qwJ8QrqReZ38qCdCWE0hQGtSTwoSaOaKS9M8h_7QgCs1SC',
            language: 'en',
            category: 'business'
          }
        });
        setNews(response.data.news);
      } catch (error) {
        console.error('Error fetching news from Currents API:', error);
      }
    };

    const fetchTopHoldings = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/portfolio/top-holdings');
        setTopHoldings(response.data);
      } catch (error) {
        console.error('Error fetching top holdings:', error);
      }
    };

    fetchNews();
    fetchTopHoldings();
  }, []);

  return (
    <div className="bg-[#1E293B] p-6 rounded-xl text-white shadow-lg space-y-6">
      <div>
        <h2 className="text-xl font-semibold mb-4">Latest News</h2>
        <ul className="list-disc list-inside max-h-48 overflow-y-auto text-gray-300">
          {news.map((item) => (
            <li key={item.id || item._id} className="mb-2">
              <a href={item.url} target="_blank" rel="noopener noreferrer" className="text-blue-400 hover:underline">
                {item.title}
              </a>
              <p className="text-sm">{item.source} - {new Date(item.published).toLocaleDateString()}</p>
            </li>
          ))}
        </ul>
      </div>
      <div>
        <h2 className="text-xl font-semibold mb-4">Top Holdings</h2>
        <ul>
          {topHoldings.length > 0 ? (
            topHoldings.map((holding) => (
              <li key={holding.stockSymbol} className="flex justify-between">
                <span>{holding.stockSymbol}</span>
                <span>{holding.quantity} shares</span>
              </li>
            ))
          ) : (
            <li>No top holdings data available.</li>
          )}
        </ul>
      </div>
    </div>
  );
}

export default NewsTopHoldings;
