import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import PortfolioSummary from './components/PortfolioSummary';
import AIChatInput from './components/AIChatInput';
import MarketDataCards from './components/MarketDataCards';
import NewsTopHoldings from './components/NewsTopHoldings';
import QuickTradeForm from './components/QuickTradeForm';
import Register from './components/Register';
import Login from './components/Login';
import Dashboard from './components/Dashboard';
import PrivateRoute from './components/PrivateRoute';

function App() {
  return (
    <Router>
      <div className="min-h-screen bg-[#0F172A] p-6 font-sans">
        <header className="flex justify-between items-center mb-6">
          <h1 className="text-3xl font-bold text-blue-400">
            VirtualTrade<span className="text-white">X</span>
          </h1>
          <div className="text-gray-300">98,765.43 Virtual USD</div>
          <div className="bg-gray-700 px-4 py-2 rounded-full">John Doe</div>
        </header>
        <Routes>
          <Route path="/" element={
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <div className="md:col-span-2 space-y-6">
                <PortfolioSummary />
                <MarketDataCards />
                <NewsTopHoldings />
              </div>
              <div className="space-y-6">
                <AIChatInput />
                <QuickTradeForm />
              </div>
            </div>
          } />
          <Route path="/register" element={<Register />} />
          <Route path="/login" element={<Login />} />
          <Route path="/dashboard" element={
            <PrivateRoute>
              <Dashboard />
            </PrivateRoute>
          } />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
