import React from 'react';
import PortfolioSummary from './PortfolioSummary';
import MarketDataCards from './MarketDataCards';
import AIChatInput from './AIChatInput';
import GeminiChat from './GeminiChat';
import LatestFinancialNews from './LatestFinancialNews';
import TopHoldings from './TopHoldings';
import NewsTopHoldings from './NewsTopHoldings';
import QuickTradeForm from './QuickTradeForm';
import CryptoPriceCard from './CryptoPriceCard';
import CryptoMarketHighlights from './CryptoMarketHighlights';

const Dashboard = () => {
  const userName = localStorage.getItem('userName') || 'John Doe';
  const virtualBalance = '98,765.43'; // This can be dynamic if available

  const fetchGeminiContent = async (userMessage) => {
    const response = await fetch('https://generativelanguage.googleapis.com/v1beta/models/gemini-pro:generateContent?key=AIzaSyBbzgqq7-fnK8H9DaNnGeUXmdqH8913ldQ', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ contents: [{ parts: [{ text: userMessage }] }] }),
    });
    const data = await response.json();
    console.log('Gemini API response:', data);
    if (data.error) {
      setError(`Gemini error: ${data.error.message || 'Unknown error'}`);
      return;
    }
    if (!data.candidates || !data.candidates.length) {
      setError('No response from Gemini. Please try again.');
      return;
    }
    return data; // <-- Return the actual Gemini API response
  };

  return (
    <div className="min-h-screen bg-[#0F172A] text-white font-sans">
      {/* Top Navigation Bar */}
      <nav className="flex items-center justify-between px-6 py-4 bg-[#1E293B] shadow-md">
        <div className="text-2xl font-bold">
          VirtualTrade-<span className="text-[#00BFFF]">X</span>
        </div>
        <div className="flex-1 mx-6 relative">
          <input
            type="text"
            placeholder="Search for a stock (e.g., AAPL, MSFT)..."
            className="w-full rounded-md pr-10 pl-4 py-2 text-black focus:outline-none"
          />
          <div className="absolute inset-y-0 right-3 flex items-center pointer-events-none">
            <svg
              className="h-5 w-5 text-gray-400"
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M21 21l-4.35-4.35m0 0A7.5 7.5 0 1110.5 3a7.5 7.5 0 016.15 13.65z"
              />
            </svg>
          </div>
        </div>
        <div className="flex items-center space-x-6">
          <div className="text-right">
            <div className="text-sm">Virtual USD Balance</div>
            <div className="text-lg font-semibold">{virtualBalance} Virtual USD</div>
          </div>
          <div className="flex items-center space-x-2 cursor-pointer relative">
            <div className="w-8 h-8 rounded-full bg-gray-600 flex items-center justify-center text-white font-semibold">
              {userName.charAt(0).toUpperCase()}
            </div>
            <div>{userName}</div>
            <button
              onClick={() => {
                localStorage.removeItem('token');
                localStorage.removeItem('refreshToken');
                localStorage.removeItem('userName');
                window.location.href = '/login';
              }}
              className="ml-4 px-3 py-1 bg-red-600 rounded text-white hover:bg-red-700 transition"
              title="Logout"
            >
              Logout
            </button>
          </div>
        </div>
      </nav>

      {/* Dashboard Sections */}
      <main className="p-6 grid grid-cols-1 md:grid-cols-3 gap-6">
        {/* Left Column */}
        <div className="space-y-6">
          {/* My Portfolio Card */}
          <PortfolioSummary />

          {/* Latest Financial News Card */}
          <LatestFinancialNews />

          <CryptoPriceCard />

          {/* Gemini Chat */}
          <GeminiChat />
        </div>

        {/* Center Column */}
        <div className="space-y-6">
          {/* Market Highlights Card */}
          <MarketDataCards />
        </div>

        {/* Right Column */}
        <div className="flex flex-col items-end md:pl-6 space-y-6">
          {/* Crypto Market Highlights Card at the top right */}
          <div className="w-full md:w-50">
            <CryptoMarketHighlights 
              className="h-full min-h-[400px]" // Adjust min-h to match MarketDataCards if needed
            />
          </div>
          {/* Quick Trade Form under Crypto Market Highlights */}
          <div className="w-full md:w-50">
            <QuickTradeForm />
          </div>
        </div>
      </main>
    </div>
  );
};

export default Dashboard;
