import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';

function useLatestNews() {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchNews = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get('/api/news/latest');
      setNews(response.data);
    } catch (err) {
      setError('Failed to load news. Please try again later.');
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchNews();
  }, [fetchNews]);

  return { news, loading, error, fetchNews };
}

function formatTimeAgo(dateString) {
  if (!dateString) return '';
  const date = new Date(dateString);
  const now = new Date();
  const diffMs = now - date;
  const diffSeconds = Math.floor(diffMs / 1000);
  if (diffSeconds < 60) return 'just now';
  const diffMinutes = Math.floor(diffSeconds / 60);
  if (diffMinutes < 60) return `${diffMinutes} minute${diffMinutes > 1 ? 's' : ''} ago`;
  const diffHours = Math.floor(diffMinutes / 60);
  if (diffHours < 24) return `${diffHours} hour${diffHours > 1 ? 's' : ''} ago`;
  const diffDays = Math.floor(diffHours / 24);
  return `${diffDays} day${diffDays > 1 ? 's' : ''} ago`;
}

export default function LatestFinancialNews() {
  const { news, loading, error, fetchNews } = useLatestNews();

  return (
    <section
      aria-labelledby="latest-news-heading"
      className="bg-[#1E293B] p-6 rounded-xl text-white shadow-lg max-h-96 overflow-y-auto"
    >
      <h2 id="latest-news-heading" className="text-xl font-semibold mb-4">
        Latest Financial News
      </h2>

      {loading && (
        <div role="status" aria-live="polite" className="flex justify-center items-center py-10">
          <svg
            className="animate-spin h-8 w-8 text-blue-400"
            xmlns="http://www.w3.org/2000/svg"
            fill="none"
            viewBox="0 0 24 24"
            aria-hidden="true"
          >
            <circle
              className="opacity-25"
              cx="12"
              cy="12"
              r="10"
              stroke="currentColor"
              strokeWidth="4"
            ></circle>
            <path
              className="opacity-75"
              fill="currentColor"
              d="M4 12a8 8 0 018-8v4a4 4 0 00-4 4H4z"
            ></path>
          </svg>
          <span className="sr-only">Loading news...</span>
        </div>
      )}

      {error && (
        <div role="alert" className="text-red-500 mb-4">
          <p>{error}</p>
          <button
            onClick={fetchNews}
            className="mt-2 px-4 py-2 bg-blue-600 hover:bg-blue-700 rounded focus:outline-none focus:ring-2 focus:ring-blue-400"
            aria-label="Retry loading news"
          >
            Retry
          </button>
        </div>
      )}

      {!loading && !error && (
        <ul className="space-y-3" aria-live="polite" aria-relevant="additions removals">
          {news.length === 0 ? (
            <li>No news available.</li>
          ) : (
            news.map((item) => (
              <li
                key={item.id || item._id}
                className="hover:bg-[#0F172A] p-2 rounded cursor-pointer"
              >
                <a
                  href={item.url}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-blue-400 font-medium hover:underline"
                >
                  {item.title}
                </a>
                <div className="text-gray-400 text-sm">
                  {item.source} – {formatTimeAgo(item.published)}
                </div>
              </li>
            ))
          )}
        </ul>
      )}
    </section>
  );
}
