import React, { useState, useRef, useEffect } from 'react';
import PortfolioSummary from './PortfolioSummary';
import MarketDataCards from './MarketDataCards';
import AIChatInput from './AIChatInput';
import GeminiChat from './GeminiChat';
import LatestFinancialNews from './LatestFinancialNews';
import TopHoldings from './TopHoldings';
import NewsTopHoldings from './NewsTopHoldings';
import QuickTradeForm from './QuickTradeForm';
import CryptoPriceCard from './CryptoPriceCard';
import CryptoMarketHighlights from './CryptoMarketHighlights';

const Dashboard = () => {
  const storedUserName = localStorage.getItem('userName') || 'John Doe';
  const [userName, setUserName] = useState(storedUserName);
  const [editingName, setEditingName] = useState(false);
  const [tempName, setTempName] = useState(userName);
  const [menuOpen, setMenuOpen] = useState(false);
  const menuRef = useRef(null);

  const [geminiMessages, setGeminiMessages] = useState([]); // NEW
  const [error, setError] = useState(null); // NEW

  useEffect(() => {
    function handleClickOutside(event) {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setMenuOpen(false);
        setEditingName(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  useEffect(() => {
    localStorage.setItem('userName', userName);
  }, [userName]);

  const fetchGeminiContent = async (userMessage) => {
    setError(null);
    try {
      const response = await fetch('https://generativelanguage.googleapis.com/v1beta/models/gemini-pro:generateContent?key=AIzaSyCrmtNEfgixC_LUa0ViraM681vwEjFCkAM', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ contents: [{ parts: [{ text: userMessage }] }] }),
      });

      const data = await response.json();
      console.log('Gemini API response:', data);

      if (data.error) {
        setError(`Gemini error: ${data.error.message || 'Unknown error'}`);
        return;
      }

      if (!data.candidates || !data.candidates.length) {
        setError('No response from Gemini. Please try again.');
        return;
      }

      const geminiReply = data.candidates[0].content.parts.map(p => p.text).join('\n');
      setGeminiMessages(prev => [...prev, { role: 'user', text: userMessage }, { role: 'gemini', text: geminiReply }]);
    } catch (err) {
      setError('Failed to fetch Gemini response.');
      console.error(err);
    }
  };

  return (
    <div className="min-h-screen bg-[#0F172A] text-white font-sans">
      {/* Top Navigation */}
      <nav className="flex items-center justify-between px-6 py-4 bg-[#1E293B] shadow-md">
        <div className="text-2xl font-bold">VirtualTrade-<span className="text-[#00BFFF]">X</span></div>

        {/* Search */}
        <div className="flex items-center space-x-4 md:space-x-6">
          <input
            type="text"
            placeholder="Search for a stock (e.g., AAPL, MSFT)..."
            className="w-full rounded-md pr-10 pl-4 py-2 text-black focus:outline-none"
          />
          <div className="absolute inset-y-0 right-3 flex items-center pointer-events-none">
            <svg className="h-5 w-5 text-gray-400" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-4.35-4.35m0 0A7.5 7.5 0 1110.5 3a7.5 7.5 0 016.15 13.65z" />
            </svg>
          </div>
        </div>

        {/* User Menu */}
        <div className="relative" ref={menuRef}>
          <button className="flex items-center space-x-2 px-3 py-2 rounded bg-gray-700 hover:bg-gray-600 transition" onClick={() => setMenuOpen(prev => !prev)}>
            <span className="w-8 h-8 rounded-full bg-gray-600 flex items-center justify-center text-white font-semibold">{userName.charAt(0).toUpperCase()}</span>
            <span>{userName}</span>
            <svg className="w-4 h-4 ml-1" fill="none" stroke="currentColor" strokeWidth={2} viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" d="M19 9l-7 7-7-7" /></svg>
          </button>
          {menuOpen && (
            <div className="absolute right-0 mt-2 w-56 bg-white text-black rounded shadow-lg z-50">
              <div className="px-4 py-2 border-b font-semibold flex items-center justify-between">
                {editingName ? (
                  <>
                    <input className="border rounded px-2 py-1 text-black w-28" value={tempName} onChange={e => setTempName(e.target.value)} onKeyDown={e => {
                      if (e.key === 'Enter') {
                        setUserName(tempName.trim() || 'John Doe');
                        setEditingName(false);
                      }
                    }} autoFocus />
                    <button className="ml-2 text-green-600 font-bold" onClick={() => {
                      setUserName(tempName.trim() || 'John Doe');
                      setEditingName(false);
                    }}>Save</button>
                  </>
                ) : (
                  <>
                    <span>{userName}</span>
                    <button className="ml-2 text-blue-600 underline text-xs" onClick={() => {
                      setTempName(userName);
                      setEditingName(true);
                    }}>Edit</button>
                  </>
                )}
              </div>
              <button className="w-full text-left px-4 py-2 hover:bg-gray-100" onClick={() => alert('Settings coming soon!')}>⚙️ Settings</button>
              <button className="w-full text-left px-4 py-2 hover:bg-gray-100" onClick={() => {
                localStorage.removeItem('token');
                localStorage.removeItem('refreshToken');
                localStorage.removeItem('userName');
                window.location.href = '/login';
              }}>🚪 Logout</button>
            </div>
          )}
        </div>
      </nav>

      {/* Dashboard Sections */}
      <main className="p-6 grid grid-cols-1 md:grid-cols-3 gap-6">
        {/* Left */}
        <div className="space-y-6">
          <PortfolioSummary />
          <LatestFinancialNews />
          <CryptoPriceCard />
        </div>

        {/* Center */}
        <div className="space-y-6">
          <MarketDataCards />
          {/* Gemini AI Chat section */}
          <div className="bg-[#1E293B] rounded-lg p-4">
            <h2 className="text-xl font-semibold mb-2">💬 AI Assistant</h2>
            <GeminiChat messages={geminiMessages} />
            {error && <p className="text-red-500 mt-2">{error}</p>}
            <AIChatInput onSend={fetchGeminiContent} />
          </div>
        </div>

        {/* Right */}
        <div className="flex flex-col items-end md:pl-6 space-y-6">
          <div className="w-full md:w-50">
            <CryptoMarketHighlights className="h-full min-h-[400px]" />
          </div>
          <div className="w-full md:w-50">
            <QuickTradeForm />
          </div>
        </div>
      </main>
    </div>
  );
};

export default Dashboard;
