import React, { useEffect, useState } from 'react';
import axios from 'axios';

function MarketDataCards() {
  const [marketData, setMarketData] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/market-highlights')
      .then(response => {
        setMarketData(response.data);
      })
      .catch(error => {
        console.error('Error fetching market data:', error);
      });
  }, []);

  return (
    <div className="bg-[#1E293B] p-6 rounded-xl text-white shadow-lg">
      <h2 className="text-xl font-semibold mb-4">Market Highlights</h2>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {marketData.map((item) => (
          <div key={item.stockSymbol} className="border border-gray-700 p-3 rounded-md">
            <h3 className="font-bold">{item.stockSymbol}</h3>
            <p>Price: ${item.price.toFixed(2)}</p>
            <p>Change: {item.change.toFixed(2)}</p>
            <p>Percent Change: {item.percentChange.toFixed(2)}%</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default MarketDataCards;
