import React, { useState, useRef, useEffect } from 'react';
import PortfolioSummary from './PortfolioSummary';
import MarketDataCards from './MarketDataCards';
import AIChatInput from './AIChatInput';
import GeminiChat from './GeminiChat';
import LatestFinancialNews from './LatestFinancialNews';
import TopHoldings from './TopHoldings';
import NewsTopHoldings from './NewsTopHoldings';
import QuickTradeForm from './QuickTradeForm';
import CryptoPriceCard from './CryptoPriceCard';
import CryptoMarketHighlights from './CryptoMarketHighlights';

const Dashboard = () => {
  const storedUserName = localStorage.getItem('userName') || 'John Doe';
  const [userName, setUserName] = useState(storedUserName);
  const [editingName, setEditingName] = useState(false);
  const [tempName, setTempName] = useState(userName);
  const [menuOpen, setMenuOpen] = useState(false);
  const menuRef = useRef(null);

  useEffect(() => {
    function handleClickOutside(event) {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setMenuOpen(false);
        setEditingName(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  useEffect(() => {
    localStorage.setItem('userName', userName);
  }, [userName]);

  const fetchGeminiContent = async (userMessage) => {
    const response = await fetch(
      'https://generativelanguage.googleapis.com/v1beta/models/gemini-pro:generateContent?key=AIzaSyCrmtNEfgixC_LUa0ViraM681vwEjFCkAM',
      {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ contents: [{ parts: [{ text: userMessage }] }] }),
      }
    );
    const data = await response.json();
    console.log('Gemini API response:', data);
    return data;
  };

  return (
    <div className="min-h-screen bg-gradient-to-tr from-[#0f172a] to-[#1e293b] text-white font-sans">
      {/* Top Navigation Bar */}
      <nav className="flex items-center justify-between px-6 py-4 bg-[#111827] shadow-lg">
        <div className="text-2xl font-bold tracking-wide text-white">
          <span className="text-[#00BFFF]">Virtual</span>Trade
          <span className="text-[#10B981]">-X</span>
        </div>

        {/* Search */}
        <div className="relative w-72">
          <input
            type="text"
            placeholder="Search stocks (AAPL, MSFT)..."
            className="w-full rounded-full pr-10 pl-4 py-2 text-black focus:outline-none shadow"
          />
          <div className="absolute inset-y-0 right-3 flex items-center pointer-events-none">
            <svg className="h-5 w-5 text-gray-400" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-4.35-4.35M16.65 16.65A7.5 7.5 0 1110.5 3a7.5 7.5 0 016.15 13.65z" />
            </svg>
          </div>
        </div>

        {/* User Menu */}
        <div className="relative" ref={menuRef}>
          <button
            onClick={() => setMenuOpen((open) => !open)}
            className="flex items-center gap-2 px-4 py-2 rounded-full bg-[#374151] hover:bg-[#4b5563] transition"
          >
            <span className="w-8 h-8 bg-[#6b7280] rounded-full flex items-center justify-center font-bold">
              {userName.charAt(0).toUpperCase()}
            </span>
            <span className="hidden md:inline">{userName}</span>
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
            </svg>
          </button>

          {menuOpen && (
            <div className="absolute right-0 mt-2 w-60 bg-white text-black rounded-lg shadow-xl z-50 overflow-hidden">
              <div className="px-4 py-2 border-b flex justify-between items-center">
                {editingName ? (
                  <>
                    <input
                      value={tempName}
                      onChange={(e) => setTempName(e.target.value)}
                      className="text-sm border px-2 py-1 rounded"
                      onKeyDown={(e) => {
                        if (e.key === 'Enter') {
                          setUserName(tempName.trim() || 'John Doe');
                          setEditingName(false);
                        }
                      }}
                      autoFocus
                    />
                    <button
                      onClick={() => {
                        setUserName(tempName.trim() || 'John Doe');
                        setEditingName(false);
                      }}
                      className="text-green-600 text-sm font-semibold ml-2"
                    >
                      Save
                    </button>
                  </>
                ) : (
                  <>
                    <span className="font-medium">{userName}</span>
                    <button
                      onClick={() => {
                        setTempName(userName);
                        setEditingName(true);
                      }}
                      className="text-blue-500 underline text-sm"
                    >
                      Edit
                    </button>
                  </>
                )}
              </div>
              <button
                onClick={() => alert('Settings coming soon!')}
                className="block w-full text-left px-4 py-2 hover:bg-gray-100 text-sm"
              >
                ‚öôÔ∏è Settings
              </button>
              <button
                onClick={() => {
                  localStorage.clear();
                  window.location.href = '/login';
                }}
                className="block w-full text-left px-4 py-2 hover:bg-gray-100 text-sm"
              >
                üö™ Logout
              </button>
            </div>
          )}
        </div>
      </nav>

      {/* Main Dashboard Content */}
      <main className="p-6 grid grid-cols-1 md:grid-cols-3 gap-6">
        {/* Left Column */}
        <div className="space-y-6">
          <PortfolioSummary />
          <LatestFinancialNews />
          <CryptoPriceCard />
        </div>

        {/* Center Column */}
        <div className="space-y-6">
          <MarketDataCards />
        </div>

        {/* Right Column */}
        <div className="space-y-6 md:pl-4">
          <CryptoMarketHighlights className="min-h-[400px]" />
          <QuickTradeForm />
        </div>
      </main>
    </div>
  );
};

export default Dashboard;
